import React, { useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import ReactPlayer from "react-player";

export const Cardscontent = () => {
  let location = useLocation();

  useEffect(() => {
    // Scroll to the top of the page when the component mounts
    window.scrollTo(0, 0);
  }, []);

  return (
    <div className="container flex justify-center items-center min-h-screen">
      <div className="card-wrapper bg-white rounded-lg shadow-md px-4 py-8 max-w-2xl flex flex-col">
        <header className="card-header flex justify-between items-center pb-4 border-b border-gray-200">
          <h1 className="card-title text-xl font-bold">{location.state.title}</h1>
          <Link to="/">
            <button className="back-button px-4 py-2 rounded-md bg-green-500 text-white hover:bg-green-700">
              Back to menu
            </button>
          </Link>
        </header>
        <main className="card-content flex flex-col gap-4">
          <div className="media-section flex flex-col items-center">
            <ReactPlayer width="100%" height="250px" controls url={location.state.videoId} />
            <img
              className="card-image rounded-lg mt-4 object-cover w-full"
              src={location.state.imageId}
              alt={location.state.title}
            />
          </div>
          <div className="text-section flex flex-col gap-4">
            <div className="details">
              <h2 className="detail-title text-xl font-bold">Details</h2>
              <p className="detail-text text-gray-700">
                {location.state.mealDetail} {/* Assuming mealDetail is valid */}
              </p>
            </div>
            <div className="info-section">
              <div className="info-row flex items-center">
                <p className="info-label text-gray-500 font-medium">Type:</p>
                <p className="info-value ml-2">{location.state.type}</p>
              </div>
              <div className="info-row flex items-center">
                <h3 className="info-label text-gray-500 font-medium">Ingredients</h3>
                <p className="info-value ml-2">
                  {location.state?.ingredients.join(", ")}
                </p>
              </div>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};
